def get_sol(): return Solution()
class Solution:
    def shortestPath(self, grid: List[List[int]], k: int) -> int:
        def inBoundary(x,y): return 0<=x<m and 0<=y<n
        m,n=len(grid),len(grid[0])
        start=(0,0,k) # i,j,no_obstacles_broken
        vis=set()
        q=deque()
        q.append(start)
        res=0
        while q:
            for _ in range(len(q)):
                x,y,k=q.popleft()
                if (x,y)==(m-1,n-1): return res
                if (x,y,k) in vis: continue
                vis.add((x,y,k))
                for dx,dy in [(1,0),(0,1),(-1,0),(0,-1)]:
                    X,Y=x+dx,y+dy
                    if not inBoundary(X,Y): continue
                    if grid[X][Y]==0:
                        q.append((X,Y,k))
                    elif grid[X][Y]==1 and k:
                        q.append((X,Y,k-1))
            res+=1
        return -1


