from leetcode.lc1351 import *
import unittest
class MyTestCase(unittest.TestCase):

    def test_1(self):
        solution = Solution()
        self.assertEqual(8, solution.countNegatives([[4,3,2,-1],[3,2,1,-1],[1,1,-1,-2],[-1,-1,-2,-3]]))

    def test_2(self):
        solution = Solution()
        self.assertEqual(0, solution.countNegatives([[3,2],[1,0]]))

    def test_3(self):
        solution = Solution()
        self.assertEqual(3, solution.countNegatives([[1,-1],[-1,-1]]))

    def test_4(self):
        solution = Solution()
        self.assertEqual(1, solution.countNegatives([[-1]]))

