import itertools; import math; import operator; import random; from bisect import *; from collections import deque, defaultdict, Counter, OrderedDict; from functools import reduce, lru_cache; from heapq import *; import unittest; from typing import List;
def get_sol(): return Solution()
class Solution:
    def minReorder(self, n: int, connections: List[List[int]]) -> int:
        CHANGE_REQUIRED=True
        g=[list() for _ in range(n)]
        for a,b in connections:
            g[a].append((b,CHANGE_REQUIRED))
            g[b].append((a,not CHANGE_REQUIRED))
        # for x in g: print(x)
        vis=set()
        self.cnt=0
        def dfs(u):
            vis.add(u)
            for v,required in g[u]:
                if v not in vis:
                    if required:
                        self.cnt+=1
                    dfs(v)

        dfs(0)
        return self.cnt
class Solution2:
    def minReorder(self, n: int, connections: List[List[int]]) -> int:
        CHANGE_REQUIRED=True
        g=[list() for _ in range(n)]
        for a,b in connections:
            g[a].append((b,CHANGE_REQUIRED))
            g[b].append((a,not CHANGE_REQUIRED))
        # for x in g: print(x)
        vis=set()
        self.cnt=0
        def dfs(u):
            if u in vis: return
            vis.add(u)
            for v,required in g[u]:
                if required:
                    if v not in vis:
                        self.cnt+=1
                dfs(v)
        dfs(0)
        return self.cnt

