import itertools; import math; import operator; import random; from bisect import *; from collections import deque, defaultdict, Counter, OrderedDict; from heapq import *; import unittest; from typing import List;
def get_sol(): return Solution()
class Solution:
    def findSubstring(self, s:str, words:List[str])->List[int]:
        wordBag = Counter(words)   # count the freq of each word
        wordLen  = len(words[0])
        numWords = len(words)
        totalLen  = wordLen*numWords
        res=[]
        for i in range(len(s)-totalLen+1):   # scan through s
            # For each i, determine if s[i:i+totalLen] is valid
            curBag = defaultdict(int)   # reset for each i
            for j in range(i, i+totalLen, wordLen):
                curWord = s[j:j+wordLen]
                curBag[curWord] += 1
                if curWord not in wordBag: break # optimization
                if curBag[curWord] > wordBag[curWord]: break # optimization
            if curBag == wordBag:
                res.append(i)   # store result
        return res

class Solution3:
    def findSubstring(self, s: str, words: List[str]) -> List[int]:
        n=len(s)
        wordLen=len(words[0])
        totalLen=wordLen*len(words)
        res=[]

        for i in range(len(s)):
            if i+totalLen>n: break
            j=i
            count = Counter(words)
            while j<n:
                curWord=s[j:j+wordLen]
                if count[curWord]==0: break
                count[curWord]-=1
                j+=wordLen
            if j-i==totalLen:
                res.append(i)
        return res
class Solution2:
    # slower
    def findSubstring(self, s: str, words: List[str]) -> List[int]:
        n=len(s)
        wordLen=len(words[0])
        totalLen=wordLen*len(words)
        res=[]

        for i in range(len(s)):
            j=i
            count = Counter(words) # this line makes it slower
            while j<n:
                curWord=s[j:j+wordLen]
                if count[curWord]==0: break
                count[curWord]-=1
                j+=wordLen
            if j-i==totalLen:
                res.append(i)
        return res


